loadTestRunner.loadTest.testCase.setPropertyValue("studyExt", "7216")

con:config.script:
def index = context.ThreadIndex
testRunner.testCase.testSteps["AEReqProps"].setPropertyValue( "index", String.valueOf(index))

/*
 * For failure, change the test case study root and ext to 7216, which is present only in caAERS
 * This will cause caTissue registration to fail and caAERS to rollback
 * 
 * for mixed success and failure, change only in every 3rd msg
 */
if ((index+1) % 3 == 0) {
	testRunner.testCase.setPropertyValue("studyRoot", "7216")
	testRunner.testCase.setPropertyValue("studyExt", "7216")
} else {
	testRunner.testCase.setPropertyValue("studyRoot", "6482")
	testRunner.testCase.setPropertyValue("studyExt", "6482")
}

