function insertCompiledTargetResponse(varResponseXML, varTargetDetailCode, varTargetResponseStatus, varMessageID){
	logger.info(varTargetDetailCode+ " Response XML: "+varResponseXML);		
	//var dbConn = DatabaseConnectionFactory.createDatabaseConnection('org.postgresql.Driver','jdbc:postgresql://localhost:5432/mirth','mirthadmin','changeme');
	var dbConn = getIHubDatabaseConnection();

	var prepStatement = "INSERT INTO COMPILED_TARGET_RESPONSE (MESSAGE_ID, TARGET_DETAILS, COMPILED_RESPONSE, TARGET_RESPONSE_STATUS, TIME_COMPLETED) VALUES(?,?,?,?,to_timestamp(?, 'YYYY-MM-DD HH24:MI:SS'))";
	var params = new Packages.java.util.ArrayList();
	params.add(varMessageID);
	params.add(varTargetDetailCode);
	params.add(varResponseXML);
	params.add(varTargetResponseStatus);
	params.add(DateUtil.getCurrentDate("yyyy-MM-dd HH:mm:ss"));

	dbConn.executeUpdate(prepStatement, params);
	dbConn.close();	
}


