	dbConn.executeUpdate(prepStatement, params);
	dbConn.close();	
}


function getCompiledResponseResultsFromDB(varMessageID){	
	var dbConn = getIHubDatabaseConnection();
	var resultSet = dbConn.executeCachedQuery("SELECT compiled_target_response.compiled_target_response_id AS compiled_target_response_compiled_target_response_id, compiled_target_response.message_id AS compiled_target_response_message_id, compiled_target_response.target_details AS compiled_target_response_target_details, compiled_target_response.compiled_response AS compiled_target_response_compiled_response, compiled_target_response.target_response_status AS compiled_target_response_target_response_status, compiled_target_response.time_completed AS compiled_target_response_time_completed FROM compiled_target_response where message_id='"+varMessageID+"'");
	//logger.info("Compiled Result Set Size: "+resultSet.size());
	dbConn.close();
	return resultSet;
}


// This function is called by: aggregateBroadcastResponse().
function updateMessageServiceResponseInDB(varIsFailureOverallResponse, varOverallResponseString, varMessageID, varMessageStatus){
	var overallResponseStatus = Packages.gov.nih.nci.ihub.util.HubConstants.OVERALL_RESPONSE_STATUS_SUCCESS;
	if(varIsFailureOverallResponse){
		overallResponseStatus = Packages.gov.nih.nci.ihub.util.HubConstants.OVERALL_RESPONSE_STATUS_FAILURE;
	}
	//var overallResponseString = Packages.gov.nih.nci.ihub.util.IntegrationHubUtil.xmlToString(varOverallResponse);
	//logger.info("Overall Response String: "+varOverallResponseString);
	var dbConn = getIHubDatabaseConnection();
	var prepStatement = "UPDATE MESSAGE SET MESSAGE_STATUS=?, OVERALL_RESPONSE_DATA=?, OVERALL_RESPONSE_STATUS=?, TIME_COMPLETED=to_timestamp(?, 'YYYY-MM-DD HH24:MI:SS') WHERE MESSAGE_ID=?";
	var params = new Packages.java.util.ArrayList();
	params.add(varMessageStatus);
	params.add(varOverallResponseString);
	params.add(overallResponseStatus);
	params.add(DateUtil.getCurrentDate("yyyy-MM-dd HH:mm:ss"));
	params.add(varMessageID);
	dbConn.executeUpdate(prepStatement, params);
	dbConn.close();		
}


function retrieveOverallBroadcastResponseFromDB(varMessageID){
	var overallResponseString;
	try{
		var dbConn = getIHubDatabaseConnection();
		var overallResponseSQL = "SELECT message.overall_response_data AS message_overall_response_data FROM message WHERE message.message_id='"+varMessageID+"' AND message.message_status='"+Packages.gov.nih.nci.ihub.util.HubConstants.MESSAGE_STATUS_PROCESSED+"'";
		var overallResponseResults = dbConn.executeCachedQuery(overallResponseSQL);

		var totalSecondsElapsed = 0;
		while (overallResponseResults.size() < 1) {
			Packages.java.lang.Thread.sleep(1000);
			overallResponseResults = dbConn.executeCachedQuery(overallResponseSQL);
			totalSecondsElapsed++;
			if(totalSecondsElapsed >= 62){			
				break;
			}
		}
		if(overallResponseResults.next()){
			overallResponseString = overallResponseResults.getString("message_overall_response_data");			
			updateMessageServiceStatusInDB(Packages.gov.nih.nci.ihub.util.HubConstants.MESSAGE_STATUS_RESPONSE_RETURNED, varMessageID);
			dbConn.close();			
		} else {
			//initiate code to put some error response message back in the queue
		}
		return overallResponseString;

	} catch (error) {
		logger.error("Error occured in Retrieve Response From Database: "+error);
	}
}

function getIHubDatabaseConnection(){	
	var dbConn = DatabaseConnectionFactory.createDatabaseConnection(globalMap.get("database.driver"),
					globalMap.get("database.url"),globalMap.get("database.username"),globalMap.get("database.password"));	
	return dbConn;
}

function aggregateBroadcastResponse(varServiceType, varBusinessMessagePayload, varConsumerIdentifier, varMessageID){
	try{
		logger.debug("aggregateBroadcastResponse() - START");
		var activeDestinationCount = globalMap.get(varServiceType+".active.destination.count");
		logger.debug("Active Destination Count in : "+activeDestinationCount);

		var compiledResponseResultSet = getCompiledResponseResultsFromDB(varMessageID);

		var totalSecondsElapsed = 0;
		while (compiledResponseResultSet.size() < activeDestinationCount) {
			Packages.java.lang.Thread.sleep(1000);
			compiledResponseResultSet = getCompiledResponseResultsFromDB(varMessageID);
			totalSecondsElapsed++;
			if(totalSecondsElapsed >= 60){
				//initiate the code for timeout here
				break;
			}
		}

		var responseArrayList = new Packages.java.util.ArrayList();
		var potentialRollbackInvocationStrategyList = new Packages.java.util.ArrayList();
		var isFailureOverallResponse = false;
		if (compiledResponseResultSet.size() == 0){
			isFailureOverallResponse = true;
		}
		while(compiledResponseResultSet.next()){
			var compiledResponseStringToDOMDocument = Packages.gov.nih.nci.ihub.util.IntegrationHubUtil.stringToDOMDocument(
				compiledResponseResultSet.getString("compiled_target_response_compiled_response"));
			responseArrayList.add(compiledResponseStringToDOMDocument);
			var compiledTargetResponseStatus = compiledResponseResultSet.getString("compiled_target_response_target_response_status");
			if(compiledTargetResponseStatus == Packages.gov.nih.nci.ihub.util.HubConstants.TARGET_RESPONSE_STATUS_FAULT){
				isFailureOverallResponse = true;
			} else {
				var targetGlobalInvocationStrategy = globalMap.get($('ns1metadata_ns1servicetype')+"_"+compiledResponseResultSet.getString("compiled_target_response_target_details")+"_Strategy");
				if(targetGlobalInvocationStrategy == null){					
					targetGlobalInvocationStrategy = globalMap.get($('ns1metadata_ns1servicetype')+"_Strategy");
				}
				var targetInvocationStrategy = targetGlobalInvocationStrategy.getClass().newInstance();
				targetInvocationStrategy.copyCommonContents(targetGlobalInvocationStrategy);

				targetInvocationStrategy.setServiceUrl(globalMap.get(compiledResponseResultSet.getString("compiled_target_response_target_details")+"."+varServiceType+".url"));
				targetInvocationStrategy.setSubject(globalMap.get("subject"+varMessageID));
				targetInvocationStrategy.setServiceProviderName(compiledResponseResultSet.getString("compiled_target_response_target_details"));
				targetInvocationStrategy.setPayload(varBusinessMessagePayload);

				potentialRollbackInvocationStrategyList.add(targetInvocationStrategy);
			}
		}

		if(isFailureOverallResponse){
			//send rollbacks to targets that executed successfully
			delegateTargetRollbackRequest(varServiceType, potentialRollbackInvocationStrategyList, varMessageID);	
		}

		var aggregatedResponseBuilder = new Packages.gov.nih.nci.ihub.writer.ncies.common.AggregatedResponseBuilder();
		var aggregatedResponse = aggregatedResponseBuilder.buildAggregatedDocument(responseArrayList, false);		
		var generateResponseBean = new Packages.gov.nih.nci.ihub.writer.ncies.common.GenerateResponseBean();
		var overallResponseString = generateResponseBean.generateResponseFromAggregatedResponse(varConsumerIdentifier, 
								varMessageID, aggregatedResponse);
		logger.info("OverallResponseString in aggregateBroadcastResponse(): "+overallResponseString);
		updateMessageServiceResponseInDB(isFailureOverallResponse, overallResponseString, varMessageID, Packages.gov.nih.nci.ihub.util.HubConstants.MESSAGE_STATUS_PROCESSED);
		logger.debug("aggregateBroadcastResponse() - END");
		return overallResponseString;
	} catch (error) {	
		logger.error("EXCEPTION OCCURED IN MIRTH RESPONSE AGGREGATOR "+error);
	}
}

function updateMessageServiceStatusInDB(varMessageStatus, varMessageID){
	var dbConn = getIHubDatabaseConnection();
	var updateSQL = "UPDATE MESSAGE SET MESSAGE_STATUS='"+varMessageStatus+"' WHERE MESSAGE_ID='"+varMessageID+"'";
	dbConn.executeUpdate(updateSQL);
	dbConn.close();
}


