function invokeDelegationService(varDelegatedCredentialReference, varCertFilePath, varKeyFilePath, varTargetDetailsCode, varMessageID){
	try{		
		var delegateServiceBean = new Packages.gov.nih.nci.ihub.writer.ncies.infrastructure.InvokeDelegationServiceBean($('ns1metadata_ns1credentials_ns1grididentifier'), 
						varDelegatedCredentialReference, varCertFilePath, varKeyFilePath, globalMap.get("user.proxy.cache.time.to.live"));
		var subject = delegateServiceBean.invokeDelegationService();		
		logger.info("Subject in function invokeDelegationService(): "+subject);	
		return subject;
	} catch (error) {	
		logger.error("EXCEPTION OCCURED IN MIRTH DURING "+varTargetDetailsCode+" INVOCATION: "+error);
		var errorMessage = createErrorResponseXML(new Packages.java.lang.Exception(error), varTargetDetailsCode);
		insertCompiledTargetResponse(errorMessage, varTargetDetailsCode, Packages.gov.nih.nci.ihub.util.HubConstants.TARGET_RESPONSE_STATUS_FAULT, varMessageID);

		channelMap.put('IS_IHUB_PROCESSING_ERROR', true);
		return "";
	}
}

function recordMessageProcessingError(varError, varTargetDetailsCode, varMessageID, varExternalID, varBusinessMessageSchemaDefinition){
	logger.debug("recordMessageProcessingError() - START");
	channelMap.put('IS_IHUB_PROCESSING_ERROR', true);	
	var errorMessage = createErrorResponseXML(new Packages.java.lang.Exception(varError), varTargetDetailsCode);
	insertCompiledTargetResponse(errorMessage, varTargetDetailsCode, Packages.gov.nih.nci.ihub.util.HubConstants.TARGET_RESPONSE_STATUS_FAULT, varMessageID);
	var gridInvocationResult = new Packages.gov.nih.nci.ihub.writer.ncies.common.GridInvocationResult(true, 
								createErrorResponseNode(new Packages.java.lang.Exception(varError), varTargetDetailsCode).getFirstChild(), false);
	var generateResponseBean = new Packages.gov.nih.nci.ihub.writer.ncies.common.GenerateResponseBean();
	var overallResponseString = generateResponseBean.generateResponseFromTargetResponse(varExternalID, 
									varMessageID, generateResponseBean.createOutputDocument(varTargetDetailsCode, 
									new Packages.java.lang.String(""), varBusinessMessageSchemaDefinition, gridInvocationResult));
	updateMessageServiceResponseInDB(true, overallResponseString, varMessageID, Packages.gov.nih.nci.ihub.util.HubConstants.MESSAGE_STATUS_PROCESSED);
	logger.debug("recordMessageProcessingError() - END");
}

function validatePayload(varSchemaFactory, varNamespace, varServiceType, varBusinessMessagePayload){
	logger.debug("validatePayload() - START");
	var payloadValidator = new Packages.gov.nih.nci.ihub.writer.ncies.infrastructure.validation.PayloadValidator();	
	payloadValidator.setCaxchangeSchemaFactory(varSchemaFactory);			
	payloadValidator.validatePayload(Packages.gov.nih.nci.ihub.util.IntegrationHubUtil.stringToDOMDocument(
		Packages.gov.nih.nci.ihub.util.IntegrationHubUtil.getPayloadFromBusinessPayload(varBusinessMessagePayload)),
		varNamespace,varServiceType);
	logger.debug("validatePayload() - END");
}
